package utc.laptrinhweb.QLBH.model;import lombok.Data;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import java.util.Date;@Data@Entitypublic class Size {    @Id    @Column(name = "MA_KT")    private String id;    @Column(name = "TEN")    private String name;    @Column(name = "NGAY_TAO")    private Date createAt;    @Column(name = "TRANG_THAI")    private int state;}